    private void minorDeclareActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_minorDeclareActionPerformed
       String minor = getMinor.getText();
       String idText = getID.getText();
       int id = Integer.parseInt(idText);
        try (Connection connection = DatabaseConnection.getConnection();
         PreparedStatement stmt = connection.prepareStatement("UPDATE students SET minor = ? WHERE id = ?")) {
        stmt.setString(1, minor);
        stmt.setInt(2, id);
        int rowsUpdated = stmt.executeUpdate();
        if (rowsUpdated > 0) {
            JOptionPane.showMessageDialog(this, "Student minor "+minor + " updated successfully!");
        } else {
            JOptionPane.showMessageDialog(this, "Student ID not found.");
        }
    } catch (SQLException e) {
       
        JOptionPane.showMessageDialog(this,  e.getMessage());
    }
       

    }//GEN-LAST:event_minorDeclareActionPerformed

