    private void infoChangeConfirmActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_infoChangeConfirmActionPerformed
        // TODO add your handling code here:
    int id = Integer.parseInt(getID.getText());
    String email = getEmail.getText();
    String phone = getPhone.getText();
    
    try (Connection connection = DatabaseConnection.getConnection();
         PreparedStatement stmt = connection.prepareStatement("UPDATE students SET email = ?, phone = ? WHERE id = ?")) {
        stmt.setString(1, email);
        stmt.setString(2, phone);
        stmt.setInt(3, id);
        int rowsUpdated = stmt.executeUpdate();
        if (rowsUpdated > 0) {
            JOptionPane.showMessageDialog(this, "Student information updated successfully!");
        } else {
            JOptionPane.showMessageDialog(this, "No Student found with the given ID.", "Error", JOptionPane.ERROR_MESSAGE);
        }
    } catch (SQLException e) {
        JOptionPane.showMessageDialog(null,e.getMessage());
    }        
    }//GEN-LAST:event_infoChangeConfirmActionPerformed

